  methods: {
    updateDateRange({
      startDate,
      endDate,
      divSelected,
      serviceSelected
    }) {
      this.startDate = startDate;
      this.endDate = endDate;
      this.divSelected = divSelected;
      this.serviceSelected = serviceSelected;
      this.loadData(this.startDate, this.endDate, this.divSelected, this.serviceSelected);
    },
    loadData(startDate, endDate, divSelected, serviceSelected) {
      const baseURL = window.location.hostname === "localhost" ? "http://localhost:3001/api" : "https://opr-voa.azurewebsites.net/api";
      if (!baseURL) {
        console.error("VUE_APP_API_BASE_URL is not defined");
        return;
      }
      const url = `${baseURL}/web?startDate=${moment(startDate).format("YYYY-MM-DD")}&endDate=${moment(endDate).format("YYYY-MM-DD")}`;
      console.log(url);
      this.axios.get(url).then(response => {
        if (divSelected.length === 0 && serviceSelected.length === 0) this.chartData = response.data;else {
          const filteredData = response.data.filter(rd => divSelected.includes(rd.division) && serviceSelected.includes(rd.service));
          this.chartData = filteredData;
        }
      }).catch(error => {
        console.error(error);
      });
    }
  },
  created() {
    this.loadData(this.startDate, this.endDate, this.divSelected, this.serviceSelected);
  }
};
